mapper.select:
select product_id , category_id , display_order from product_categories where 1=1
and product_id in
#{id}

mapper.insert:
        insert into product_categories (
            product_id
			, category_id
			, display_order
        ) values (
            #{productId}
            , #{categoryId}
            , #{displayOrder}
        )

mapper.update:
update product_categories
set display_order = #{displayOrder}
where product_id = #{productId}
    and category_id = #{categoryId}

mapper.delete:
delete from product_categories
where product_id = #{productId}
    and category_id = #{categoryId}
