mapper.sql:

    and a.role = #{search.role}


    and a.status = #{search.status}

and (a.status is null or a.status != 'DELETED')

    and a.provider = #{search.provider}


    and a.created_at  >=  #{search.startDate}


    and a.created_at  <=  #{search.endDate}


    and a.nickname like concat('%', #{search.text}, '%')

mapper.select:
select a.id , a.username , a.role , a.status , a.nickname , a.birth_date , a.phone_number , a.last_login_at , a.created_at , u1.nickname as created_by , a.modified_at , u2.nickname as modified_by from user a left join user u1 on u1.id = a.created_by left join user u2 on u2.id = a.modified_by
order by a.created_at desc
limit #{paging.limit} offset #{paging.offset}

mapper.insert:
insert into user (
    id
  , username
  , password
  , role
  , status
  , nickname
  , birth_date
  , phone_number
  , provider
  , provider_id
  , created_by
) values (
    #{id}
  , #{username}
  , #{password}
  , #{role}
  , #{status}
  , #{nickname}
  , #{birthDate}
  , #{phoneNumber}
  , #{provider}
  , #{providerId}
  , #{createdBy}
)
